const cheerio = require('cheerio');
const extractedTweets = [];

// Process each HTTP response
for (const item of items) {
  if (item.json && item.json.data) {
    const html = item.json.data;
    const $ = cheerio.load(html);

    // Extract tweets from Nitter HTML
    $('.timeline-item').each((i, element) => {
      const $tweet = $(element);
      const $header = $tweet.find('.tweet-header');
      const $content = $tweet.find('.tweet-content');
      const $stats = $tweet.find('.tweet-stats');

      // Extract tweet data
      const username = $header.find('.username').text().trim();
      const displayName = $header.find('.fullname').text().trim();
      const tweetText = $content.find('.tweet-text').text().trim();
      const tweetLink = $header.find('.tweet-link').attr('href');
      const timestamp = $header.find('.tweet-date').attr('title');

      // Extract engagement metrics
      const likes = parseInt($stats.find('.likes').text().replace(/[^0-9]/g, '')) || 0;
      const retweets = parseInt($stats.find('.retweets').text().replace(/[^0-9]/g, '')) || 0;
      const replies = parseInt($stats.find('.replies').text().replace(/[^0-9]/g, '')) || 0;

      // Check if user is verified (blue checkmark)
      const isVerified = $header.find('.verified').length > 0;

      // Only process tweets that meet our criteria
      if (tweetText && username && likes >= 3 && isVerified) {
        // Generate unique tweet ID
        const tweetId = tweetLink ? tweetLink.split('/').pop() : `${username}_${Date.now()}`;

        extractedTweets.push({
          id: tweetId,
          username: username,
          displayName: displayName,
          text: tweetText,
          url: tweetLink ? `https://twitter.com${tweetLink}` : null,
          timestamp: timestamp || new Date().toISOString(),
          likes: likes,
          retweets: retweets,
          replies: replies,
          isVerified: isVerified,
          platform: 'Twitter',
          scraped_at: new Date().toISOString()
        });
      }
    });
  }
}

// Fallback: If Nitter fails, try alternative scraping method
if (extractedTweets.length === 0) {
  console.log('No tweets found via Nitter, trying alternative method');

  // Alternative: Use Twitter's public search (limited)
  for (const item of items) {
    if (item.json && typeof item.json === 'string') {
      const html = item.json;

      // Simple regex-based extraction as fallback
      const tweetPattern = /<article[^>]*data-testid="tweet"[^>]*>([\s\S]*?)<\/article>/g;
      let match;

      while ((match = tweetPattern.exec(html)) !== null) {
        const tweetHtml = match[1];

        // Extract basic tweet info using regex
        const textMatch = tweetHtml.match(/<div[^>]*data-testid="tweetText"[^>]*>([\s\S]*?)<\/div>/);
        const userMatch = tweetHtml.match(/<span[^>]*>@([a-zA-Z0-9_]+)<\/span>/);

        if (textMatch && userMatch) {
          const cleanText = textMatch[1].replace(/<[^>]*>/g, '').trim();
          const username = userMatch[1];

          extractedTweets.push({
            id: `${username}_${Date.now()}_${Math.random()}`,
            username: username,
            displayName: username,
            text: cleanText,
            url: `https://twitter.com/${username}`,
            timestamp: new Date().toISOString(),
            likes: 3, // Assume minimum threshold met
            retweets: 0,
            replies: 0,
            isVerified: true, // Assume verified since we're filtering for it
            platform: 'Twitter',
            scraped_at: new Date().toISOString()
          });
        }
      }
    }
  }
}

console.log(`Extracted ${extractedTweets.length} tweets`);
return extractedTweets;

// Format tweet for Discord
const tweet = $json;

// Create rich Discord embed
const embed = {
  title: `üê¶ New Tweet from @${tweet.username}`,
  description: tweet.text,
  color: 1942002, // Twitter blue color
  timestamp: new Date(tweet.timestamp).toISOString(),
  fields: [
    {
      name: "üìä Engagement",
      value: `‚ù§Ô∏è ${tweet.likes} | üîÑ ${tweet.retweets} | üí¨ ${tweet.replies}`,
      inline: true
    },
    {
      name: "üîó Link",
      value: tweet.url || `https://twitter.com/${tweet.username}`,
      inline: true
    }
  ],
  footer: {
    text: "Podha Protocol Twitter Monitor",
    icon_url: "https://pbs.twimg.com/profile_images/1234567890/twitter_icon.jpg"
  }
};

// Add verification badge if verified
if (tweet.isVerified) {
  embed.author = {
    name: `${tweet.displayName} ‚úÖ`,
    icon_url: `https://unavatar.io/twitter/${tweet.username}`
  };
}

// Add keywords that triggered the match
const keywords = [];
if (tweet.text.toLowerCase().includes('podha')) keywords.push('Podha');
if (tweet.text.toLowerCase().includes('rwa')) keywords.push('RWA');
if (tweet.text.toLowerCase().includes('real world assets')) keywords.push('Real World Assets');
if (tweet.text.toLowerCase().includes('yield')) keywords.push('Yield');
if (tweet.text.toLowerCase().includes('solana')) keywords.push('Solana');
if (tweet.text.toLowerCase().includes('bitcoin')) keywords.push('Bitcoin');
if (tweet.text.toLowerCase().includes('defi')) keywords.push('DeFi');

if (keywords.length > 0) {
  embed.fields.push({
    name: "üè∑Ô∏è Matched Keywords",
    value: keywords.join(', '),
    inline: false
  });
}

return [{
  embeds: [embed],
  username: "Podha Twitter Monitor",
  avatar_url: "https://cdn.discordapp.com/avatars/bot_id/avatar.png"
}];

// Error handling and logging
const error = $json;

console.error('Twitter scraping error:', error);

// Try alternative Nitter instances
const alternativeInstances = [
  'https://nitter.it',
  'https://nitter.42l.fr',
  'https://nitter.pussthecat.org',
  'https://nitter.fdn.fr'
];

// Return alternative instance to try
return [{
  alternative_instance: alternativeInstances[Math.floor(Math.random() * alternativeInstances.length)],
  error_message: error.message || 'Unknown error',
  timestamp: new Date().toISOString()
}];

